import React, {Component} from 'react';

class ConnectServer extends Component {

    constructor() {
        super();
        this.state = {users: []}
    }

    componentDidMount() {
        let users = fetch("api/users").then(response => response.json()).then(response => this.setState({users: response}))
    }

    prova = (e) => {
        let user = fetch("api/users/" + e.target.id).then(response => response.json()).then(response => console.log(response))
    }

    render() {
        return (
                <ul>
                    {this.state.users.map(element => (
                        <li id={element.id} key={element.id} onClick={this.prova}>{element.id} {element.firstName} {element.lastName} {element.memo} {element.number} {element.date}</li>
                    ))
                    }
                </ul>
        );
    }
}

export default ConnectServer;
