import React, {Component, useState} from 'react';

class MenuUsers extends Component {

    constructor() {
        super();
        this.state = {users: [],
            user: null,
            modifyUser : false,
        }

    }

    componentDidMount() {
        fetch("api/users").then(response => response.json()).then(response => {
            this.setState({users : response})
            this.props.setUsers(this.state.users)
        })


    }

    render() {
        return (
            <React.Fragment>
                <ul>
                    {this.state.users.map(element => (
                        <ElementList element={element}/>
                    ))
                    }
                </ul>
            </React.Fragment>
        );
    }
}

function ElementList(props) {
    const [user, setUser] = useState(null)
    const [active, setActive] = useState(false)

    let prova = (e) => {
        fetch("api/users/" + e.target.id).then(response => response.json()).then(response => {
            setUser(response)
            setActive(true)
        })
    }

    return (
        <li id={props.element.id} key={props.element.id} onClick={prova}>{props.element.id} {props.element.firstName} {props.element.lastName} {props.element.memo} {props.element.number} {props.element.date}</li>

    )
}


export default MenuUsers;
