import React, {Component} from 'react';

class MenuUsers extends Component {

    constructor() {
        super();
        this.state = {users: [],
            user: null,
            modifyUser : false,
        }

    }

    componentDidMount() {
        const users = fetch("api/users").then(response => response.json()).then(response => {
            this.setState({users : response})
            this.props.setUsers(this.state.users)
        })


    }

    prova = (e) => {
        let user = fetch("api/users/" + e.target.id).then(response => response.json()).then(response => this.setState({
            modifyUser: true,
            user: response
        }))
    }


    render() {
        return (
            <React.Fragment>
                <ul>
                    {this.state.users.map(element => (
                        <ElementList element={element}/>
                    ))
                    }
                </ul>
                {
                    this.state.modifyUser && <p>{this.state.user.id} {this.state.user.firstName} {this.state.lastName} {this.state.user.memo} {this.state.user.number} {this.state.user.date}</p>
                }
            </React.Fragment>
        );
    }
}

function ElementList(element) {
    return <li id={element.id} key={element.id} onClick={this.prova}>{element.id} {element.firstName} {element.lastName} {element.memo} {element.number} {element.date}</li>

}


export default MenuUsers;
